    24
    25export default function LifeOS() {
    26  const {
    27    currentView,
    28    modals,
    29    editingTask,
    30    userEnergyLevel,
    31    setView,
    32    openModal,
    33    closeModal,
    34    setEditingTask,
    35    navigateAndClose,
    36  } = useAppStore()
    37
    38  const {
    39    currentTask,
    40    tasks,
    41    loading: tasksLoading,
    42    completeTask,
    43    skipTask,
    44    addTask,
    45    deleteTask,
    46    updateTask,
    47    getAllTasks,
    48    resortForEnergy,
    49    refetch,
    50  } = useTasks()
    51  const { stats, loading: statsLoading } = useUserStats()
    52  const { isStuck, stuckInfo, recordSkip } = useStuckDetection(currentTask?.id)
