    54  const router = useRouter()
    55  const supabase = createClient()
    56
    57  const processingTasksRef = useRef<Set<string>>(new Set())
    58
    59  useEffect(() => {
    60    const checkAuth = async () => {
    61      const {
    62        data: { user },
    63      } = await supabase.auth.getUser()
    64      if (!user) {
    65        router.push("/auth/login")
    66      }
    67    }
    68    checkAuth()
    69  }, [router, supabase])
    70
    71  useEffect(() => {
    72    const handleKeyDown = (e: KeyboardEvent) => {
    73      if ((e.metaKey || e.ctrlKey) && e.key === "k") {
    74        e.preventDefault()
    75        openModal("commandPalette")
    76      }
    77    }
    78
    79    window.addEventListener("keydown", handleKeyDown)
    80    return () => window.removeEventListener("keydown", handleKeyDown)
    81  }, [openModal])
    82
    83  useEffect(() => {
    84    if (currentView === "taskList") {
    85      loadAllTasks()
    86    }
    87  }, [currentView])
    88
    89  const [allTasks, setAllTasks] = useState<any[]>([])
    90
    91  useEffect(() => {
    92    if (allTasks.length > 0 && tasks.length > 0) {
    93      const activeTasks = new Set(tasks.map((t) => t.id))
    94
    95      setAllTasks((prev) => {
    96        const updated = prev.map((t) => {
    97          // If a task is NOT in useTasks (which only has pending), mark it completed
    98          if (!activeTasks.has(t.id) && !t.completed && !t.skipped) {
    99            console.log("[v0] Syncing task to completed in allTasks:", t.title)
   100            return { ...t, completed: true, completed_at: new Date().toISOString() }
   101          }
   102          return t
   103        })
   104        return updated
   105      })
   106    }
   107  }, [tasks, allTasks.length])
   108
   109  const loadAllTasks = async () => {
   110    const allTasksData = await getAllTasks()
   111    setAllTasks(allTasksData)
   112  }
   113
   114  const handleComplete = async () => {
   115    if (!currentTask) return
   116
   117    if (processingTasksRef.current.has(currentTask.id)) {
   118      console.log("[v0] handleComplete - task already being processed, skipping")
   119      return
   120    }
   121
   122    processingTasksRef.current.add(currentTask.id)
   123    try {
   124      await completeTask(currentTask.id)
   125    } finally {
   126      setTimeout(() => {
   127        processingTasksRef.current.delete(currentTask.id)
   128      }, 1000)
   129    }
   130  }
   131
   132  const handleAddVoiceTask = async (taskTitle: string, date?: string, time?: string) => {
   133    const deadline = date ? new Date(date).toISOString() : undefined
   134    await addTask({
   135      title: taskTitle,
   136      description: time ? `Scheduled for ${time}` : undefined,
   137      deadline,
   138      priority: "medium",
   139      estimated_minutes: 15,
   140    })
   141    refetch()
   142  }
   143
   144  const handleAddTaskSubmit = async (taskData: any) => {
   145    console.log("[v0] handleAddTaskSubmit called with:", taskData)
   146    console.log("[v0] handleAddTaskSubmit - editingTask:", editingTask?.id || "none")
   147
   148    if (editingTask) {
   149      console.log("[v0] handleAddTaskSubmit - updating existing task")
   150      await updateTask(editingTask.id, taskData)
   151      setEditingTask(null)
   152    } else {
   153      console.log("[v0] handleAddTaskSubmit - creating new task")
   154      const result = await addTask(taskData)
   155      console.log("[v0] handleAddTaskSubmit - addTask result:", result)
   156    }
   157
   158    await loadAllTasks()
   159    refetch()
   160  }
   161
   162  const handleToggleComplete = async (id: string) => {
   163    console.log("[v0] handleToggleComplete called for task:", id)
   164
   165    if (processingTasksRef.current.has(id)) {
   166      console.log("[v0] handleToggleComplete - task already being processed, skipping duplicate call")
   167      return
   168    }
   169
   170    const task = allTasks.find((t) => t.id === id)
   171    console.log("[v0] handleToggleComplete - task found:", task?.title, "completed:", task?.completed)
   172
   173    if (!task) {
   174      console.log("[v0] handleToggleComplete - task not found in allTasks")
   175      return
   176    }
   177
   178    processingTasksRef.current.add(id)
   179    console.log("[v0] handleToggleComplete - marked as processing")
   180
   181    try {
   182      if (task.completed) {
   183        console.log("[v0] handleToggleComplete - marking as incomplete")
   184        await updateTask(id, { completed: false, completed_at: undefined })
   185      } else {
   186        console.log("[v0] handleToggleComplete - marking as complete")
   187        await completeTask(id)
   188      }
   189
   190      const newCompletedState = !task.completed
   191      setAllTasks((prev) =>
   192        prev.map((t) =>
   193          t.id === id
   194            ? {
   195                ...t,
   196                completed: newCompletedState,
   197                completed_at: newCompletedState ? new Date().toISOString() : undefined,
   198              }
   199            : t,
   200        ),
   201      )
   202      console.log("[v0] handleToggleComplete - local state updated, completed:", newCompletedState)
   203
   204      refetch()
   205      console.log("[v0] handleToggleComplete - useTasks refetch triggered (no loadAllTasks)")
   206    } catch (error) {
   207      console.error("[v0] handleToggleComplete - error:", error)
   208      setAllTasks((prev) =>
   209        prev.map((t) => (t.id === id ? { ...t, completed: task.completed, completed_at: task.completed_at } : t)),
