"use client"

import { useState, useRef } from "react"
import { useEffect } from "react"
import { ViewManager } from "@/components/view-manager"
import { CommandPalette } from "@/components/command-palette"
import { VoiceReminderScreen } from "@/components/voice-reminder-screen"
import { Mic, Loader2 } from "lucide-react"
import { motion } from "framer-motion"
import { useTasks } from "@/hooks/use-tasks"
import { useUserStats } from "@/hooks/use-user-stats"
import { createClient } from "@/lib/supabase/client"
import { useRouter } from "next/navigation"
import { AddTaskForm } from "@/components/add-task-form"
import { MenuDrawer } from "@/components/menu-drawer"
import { generateTaskReasoning } from "@/lib/generate-reasoning"
import { useStuckDetection } from "@/hooks/use-stuck-detection"
import { useDailyPlanning } from "@/hooks/use-daily-planning"
import { StuckTaskModal } from "@/components/stuck-task-modal"
import { TaskBreakdownModal } from "@/components/task-breakdown-modal"
import { DailyPlanningModal } from "@/components/daily-planning-modal"
import { CompletionCelebration } from "@/components/completion-celebration"
import { useAppStore } from "@/lib/stores/app-store"

export default function LifeOS() {
  const {
    currentView,
    modals,
    editingTask,
    userEnergyLevel,
    setView,
    openModal,
    closeModal,
    setEditingTask,
    navigateAndClose,
  } = useAppStore()

  const {
    currentTask,
    tasks,
    loading: tasksLoading,
    completeTask,
    skipTask,
    addTask,
    deleteTask,
    updateTask,
    getAllTasks,
    resortForEnergy,
    refetch,
  } = useTasks()
  const { stats, loading: statsLoading } = useUserStats()
  const { isStuck, stuckInfo, recordSkip } = useStuckDetection(currentTask?.id)
  const { shouldShowPlanning, completePlanning, dismissPlanning } = useDailyPlanning()
  const router = useRouter()
  const supabase = createClient()

  const processingTasksRef = useRef<Set<string>>(new Set())

  useEffect(() => {
    const checkAuth = async () => {
      const {
        data: { user },
      } = await supabase.auth.getUser()
      if (!user) {
        router.push("/auth/login")
      }
    }
    checkAuth()
  }, [router, supabase])

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.metaKey || e.ctrlKey) && e.key === "k") {
        e.preventDefault()
        openModal("commandPalette")
      }
    }

    window.addEventListener("keydown", handleKeyDown)
    return () => window.removeEventListener("keydown", handleKeyDown)
  }, [openModal])

  useEffect(() => {
    if (currentView === "taskList") {
      loadAllTasks()
    }
  }, [currentView])

  const [allTasks, setAllTasks] = useState<any[]>([])

  useEffect(() => {
    if (allTasks.length > 0 && tasks.length > 0) {
      const activeTasks = new Set(tasks.map((t) => t.id))

      setAllTasks((prev) => {
        const updated = prev.map((t) => {
          // If a task is NOT in useTasks (which only has pending), mark it completed
          if (!activeTasks.has(t.id) && !t.completed && !t.skipped) {
            console.log("[v0] Syncing task to completed in allTasks:", t.title)
            return { ...t, completed: true, completed_at: new Date().toISOString() }
          }
          return t
        })
        return updated
      })
    }
  }, [tasks, allTasks.length])

  const loadAllTasks = async () => {
    const allTasksData = await getAllTasks()
    setAllTasks(allTasksData)
  }

  const handleComplete = async () => {
    if (!currentTask) return

    if (processingTasksRef.current.has(currentTask.id)) {
      console.log("[v0] handleComplete - task already being processed, skipping")
      return
    }

    processingTasksRef.current.add(currentTask.id)
    try {
      await completeTask(currentTask.id)
    } finally {
      setTimeout(() => {
        processingTasksRef.current.delete(currentTask.id)
      }, 1000)
    }
  }

  const handleAddVoiceTask = async (taskTitle: string, date?: string, time?: string) => {
    const deadline = date ? new Date(date).toISOString() : undefined
    await addTask({
      title: taskTitle,
      description: time ? `Scheduled for ${time}` : undefined,
      deadline,
      priority: "medium",
      estimated_minutes: 15,
    })
    refetch()
  }

  const handleAddTaskSubmit = async (taskData: any) => {
    console.log("[v0] handleAddTaskSubmit called with:", taskData)
    console.log("[v0] handleAddTaskSubmit - editingTask:", editingTask?.id || "none")

    if (editingTask) {
      console.log("[v0] handleAddTaskSubmit - updating existing task")
      await updateTask(editingTask.id, taskData)
      setEditingTask(null)
    } else {
      console.log("[v0] handleAddTaskSubmit - creating new task")
      const result = await addTask(taskData)
      console.log("[v0] handleAddTaskSubmit - addTask result:", result)
    }

    await loadAllTasks()
    refetch()
  }

  const handleToggleComplete = async (id: string) => {
    console.log("[v0] handleToggleComplete called for task:", id)

    if (processingTasksRef.current.has(id)) {
      console.log("[v0] handleToggleComplete - task already being processed, skipping duplicate call")
      return
    }

    const task = allTasks.find((t) => t.id === id)
    console.log("[v0] handleToggleComplete - task found:", task?.title, "completed:", task?.completed)

    if (!task) {
      console.log("[v0] handleToggleComplete - task not found in allTasks")
      return
    }

    processingTasksRef.current.add(id)
    console.log("[v0] handleToggleComplete - marked as processing")

    try {
      if (task.completed) {
        console.log("[v0] handleToggleComplete - marking as incomplete")
        await updateTask(id, { completed: false, completed_at: undefined })
      } else {
        console.log("[v0] handleToggleComplete - marking as complete")
        await completeTask(id)
      }

      const newCompletedState = !task.completed
      setAllTasks((prev) =>
        prev.map((t) =>
          t.id === id
            ? {
                ...t,
                completed: newCompletedState,
                completed_at: newCompletedState ? new Date().toISOString() : undefined,
              }
            : t,
        ),
      )
      console.log("[v0] handleToggleComplete - local state updated, completed:", newCompletedState)

      refetch()
      console.log("[v0] handleToggleComplete - useTasks refetch triggered (no loadAllTasks)")
    } catch (error) {
      console.error("[v0] handleToggleComplete - error:", error)
      setAllTasks((prev) =>
        prev.map((t) => (t.id === id ? { ...t, completed: task.completed, completed_at: task.completed_at } : t)),
      )
    } finally {
      setTimeout(() => {
        processingTasksRef.current.delete(id)
        console.log("[v0] handleToggleComplete - removed from processing set")
      }, 1500)
    }
  }

  const handleDeleteTask = async (id: string) => {
    await deleteTask(id)
    await loadAllTasks()
    refetch()
  }

  const handleEditTask = (task: any) => {
    setEditingTask(task)
    openModal("addTask")
  }

  const handleCommandAction = (action: string) => {
    switch (action) {
      case "complete":
        handleComplete()
        break
      case "dashboard":
        navigateAndClose("dashboard")
        break
      case "taskList":
        navigateAndClose("taskList")
        break
      case "addTask":
        openModal("addTask")
        break
      case "settings":
        navigateAndClose("settings")
        break
      default:
        break
    }
  }

  const handleCantDo = () => {
    // Placeholder
  }

  if (tasksLoading || statsLoading) {
    return (
      <div className="min-h-screen bg-[#0a0f16] flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="w-12 h-12 text-cyan-400 animate-spin mx-auto mb-4" />
          <p className="text-slate-400">Loading your tasks...</p>
        </div>
      </div>
    )
  }

  const reasoning = currentTask
    ? generateTaskReasoning({
        title: currentTask.title,
        priority: currentTask.priority,
        energy_level: currentTask.energy_level,
        estimated_minutes: currentTask.estimated_minutes,
        deadline: currentTask.deadline,
        created_at: currentTask.created_at,
      })
    : { energyMatch: 0, priorityReason: "", contextNote: "" }

  const totalTasks = (stats?.total_completed || 0) + (stats?.total_skipped || 0)
  const computedCompletionRate = totalTasks > 0 ? Math.round(((stats?.total_completed || 0) / totalTasks) * 100) : 75

  const userStats = {
    currentStreak: stats?.current_streak || 0,
    trustScore: stats?.trust_score || 50,
    weeklyCompletionRate: computedCompletionRate,
    energyAccuracy: 80, // Default - not tracked in database
    avgCompletionTime: 22, // Default - not tracked in database
  }

  const surveillanceTasks = tasks
    .filter((t) => t.deadline && new Date(t.deadline) < new Date())
    .map((t) => ({
      id: t.id,
      title: t.title,
      daysOverdue: Math.floor((Date.now() - new Date(t.deadline!).getTime()) / (1000 * 60 * 60 * 24)),
    }))

  return (
    <>
      <ViewManager
        currentView={currentView}
        tasks={tasks}
        currentTask={currentTask}
        stats={userStats}
        reasoning={reasoning}
        surveillanceTasks={surveillanceTasks}
        allTasks={allTasks}
        onComplete={handleComplete}
        onCantDo={handleCantDo}
        onNavigate={(view) => navigateAndClose(view)}
        onAddTask={() => openModal("addTask")}
        onToggleComplete={handleToggleComplete}
        onDeleteTask={handleDeleteTask}
        onEditTask={handleEditTask}
        onOpenMenuDrawer={() => openModal("menuDrawer")}
        onOpenVoiceReminder={() => openModal("voiceReminder")}
      />

      <DailyPlanningModal
        isOpen={shouldShowPlanning}
        onClose={dismissPlanning}
        tasks={tasks}
        onSetEnergyLevel={(level) => completePlanning(level)}
        onStartDay={(topTasks) => {
          completePlanning(userEnergyLevel || "medium")
        }}
      />

      <StuckTaskModal
        isOpen={modals.stuckTask}
        onClose={() => closeModal("stuckTask")}
        task={currentTask ? { id: currentTask.id, title: currentTask.title } : { id: "", title: "" }}
        skipCount={stuckInfo?.skipCount || 0}
        onBreakDown={() => {
          closeModal("stuckTask")
          openModal("breakdown")
        }}
        onDelegate={() => closeModal("stuckTask")}
        onHireOut={() => closeModal("stuckTask")}
        onDelete={async () => {
          if (currentTask) await deleteTask(currentTask.id)
          closeModal("stuckTask")
        }}
        onKeep={(reason) => closeModal("stuckTask")}
      />

      <TaskBreakdownModal
        isOpen={modals.breakdown}
        onClose={() => closeModal("breakdown")}
        task={currentTask ? { id: currentTask.id, title: currentTask.title } : { id: "", title: "" }}
        userEnergy={userEnergyLevel}
        onCreateSubtasks={async (subtasks) => {
          for (const sub of subtasks) {
            await addTask({
              title: sub.title,
              estimated_minutes: sub.estimatedMinutes,
              energy_level: sub.energyLevel,
              priority: "medium",
            })
          }
          if (currentTask) {
            await completeTask(currentTask.id)
          }
          closeModal("breakdown")
          refetch()
        }}
        onReplaceWithFirst={async (subtask) => {
          if (currentTask) {
            await updateTask(currentTask.id, {
              title: subtask.title,
              estimated_minutes: subtask.estimatedMinutes,
              energy_level: subtask.energyLevel,
            })
          }
          closeModal("breakdown")
          refetch()
        }}
        onCompleteMainTask={async () => {
          if (currentTask) await completeTask(currentTask.id)
          closeModal("breakdown")
        }}
      />

      <CompletionCelebration />

      <CommandPalette
        isOpen={modals.commandPalette}
        onClose={() => closeModal("commandPalette")}
        tasks={tasks.map((t) => ({
          id: t.id,
          title: t.title,
          status: t.completed ? ("done" as const) : ("pending" as const),
          context: "Work" as const,
          tags: [],
          estimatedMinutes: t.estimated_minutes || 25,
          bestTime: "now" as const,
          friction: "medium" as const,
          size: "medium" as const,
          quickWin: false,
          delegateCandidate: false,
          vendorCandidate: false,
          createdAt: t.created_at,
          dueAt: t.deadline,
          energyLevel: t.energy_level || "medium",
          priority: t.priority || "medium",
          surveillance: t.deadline ? new Date(t.deadline) < new Date() : false,
        }))}
        onAction={handleCommandAction}
      />

      <AddTaskForm
        isOpen={modals.addTask}
        onClose={() => {
          closeModal("addTask")
          setEditingTask(null)
        }}
        onSubmit={handleAddTaskSubmit}
        initialTask={editingTask}
      />

      {modals.voiceReminder && (
        <VoiceReminderScreen onClose={() => closeModal("voiceReminder")} onAddTask={handleAddVoiceTask} />
      )}

      <MenuDrawer
        isOpen={modals.menuDrawer}
        onClose={() => closeModal("menuDrawer")}
        onNavigate={(view) => navigateAndClose(view)}
        onAddTask={() => openModal("addTask")}
      />

      {!modals.voiceReminder && currentView === "task" && (
        <motion.button
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ type: "spring", stiffness: 260, damping: 20 }}
          onClick={() => openModal("voiceReminder")}
          className="fixed bottom-6 right-6 w-16 h-16 rounded-full bg-gradient-to-br from-purple-500 to-purple-700 shadow-lg shadow-purple-500/30 flex items-center justify-center hover:shadow-xl hover:shadow-purple-500/40 transition-shadow z-40"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <Mic className="w-7 h-7 text-white" fill="currentColor" />
        </motion.button>
      )}
    </>
  )
}

import { create } from "zustand"
import { persist } from "zustand/middleware"
import { createClient } from "@/lib/supabase/client"

export type View = "task" | "dashboard" | "settings" | "taskList"
export type EnergyLevel = "peak" | "medium" | "low"
export type AITone = "encouraging" | "stoic" | "urgent"

interface ModalState {
  commandPalette: boolean
  voiceReminder: boolean
  addTask: boolean
  menuDrawer: boolean
  stuckTask: boolean
  breakdown: boolean
  dailyPlanning: boolean
  shameFreeReset: boolean
  statModal: "streak" | "trust" | null
}

interface Preferences {
  soundEnabled: boolean
  hapticEnabled: boolean
  confettiEnabled: boolean
  showTrustScore: boolean
  showStreak: boolean
  reduceAnimations: boolean
  theme: "dark" | "light"
  accentColor: string
  aiReasoningStyle: AITone
  autoBreakdown: boolean
  defaultDurationMinutes: number
  autoStartBreaks: boolean
}

interface CelebrationContext {
  isVisible: boolean
  wasQuickWin: boolean
  wasOverdue: boolean
  wasFromBreakdown: boolean
  wasPostReset: boolean
  stepsCompleted: number
  taskTitle: string
}

interface ResetState {
  lastResetDate: string | null
  resetCount: number
}

interface AppState {
  // View Management
  currentView: View
  modals: ModalState
  editingTask: any | null

  // Session State
  userEnergyLevel: EnergyLevel
  timerRunning: boolean
  timeLeft: number
  defaultTimerMinutes: number
  tasksCompletedToday: number
  lastPlanningDate: string | null

  // Preferences State
  preferences: Preferences

  // Celebration State
  celebration: CelebrationContext

  // Reset State
  resetState: ResetState

  // View Actions
  setView: (view: View) => void
  openModal: (modal: keyof Omit<ModalState, "statModal">) => void
  closeModal: (modal: keyof Omit<ModalState, "statModal">) => void
  openStatModal: (type: "streak" | "trust") => void
  closeStatModal: () => void
  closeAllModals: () => void
  setEditingTask: (task: any | null) => void
  navigateAndClose: (view: View) => void

  // Session Actions
  setUserEnergy: (level: EnergyLevel) => void
  setTimerRunning: (running: boolean) => void
  setTimeLeft: (seconds: number) => void
  decrementTimer: () => void
  resetTimer: () => void
  incrementTasksCompleted: () => void
  completePlanning: () => void
  shouldShowPlanning: () => boolean

  // Preference Actions
  updatePreference: <K extends keyof Preferences>(key: K, value: Preferences[K]) => void

  // Celebration Actions
  triggerCelebration: (ctx: Partial<Omit<CelebrationContext, "isVisible">>) => void
  dismissCelebration: () => void

  // Reset Actions
  shameFreeReset: () => Promise<{ success: boolean; message?: string }>
  isPostReset: () => boolean
}

const initialModals: ModalState = {
  commandPalette: false,
  voiceReminder: false,
  addTask: false,
  menuDrawer: false,
  stuckTask: false,
  breakdown: false,
  dailyPlanning: false,
  shameFreeReset: false,
  statModal: null,
}

const initialPreferences: Preferences = {
  soundEnabled: true,
  hapticEnabled: true,
  confettiEnabled: true,
  showTrustScore: true,
  showStreak: true,
  reduceAnimations: false,
  theme: "dark",
  accentColor: "cyan",
  aiReasoningStyle: "encouraging",
  autoBreakdown: false,
  defaultDurationMinutes: 25,
  autoStartBreaks: false,
}

const initialCelebration: CelebrationContext = {
  isVisible: false,
  wasQuickWin: false,
  wasOverdue: false,
  wasFromBreakdown: false,
  wasPostReset: false,
  stepsCompleted: 0,
  taskTitle: "",
}

const initialResetState: ResetState = {
  lastResetDate: null,
  resetCount: 0,
}

export const useAppStore = create<AppState>()(
  persist(
    (set, get) => ({
      // Initial State
      currentView: "task",
      modals: initialModals,
      editingTask: null,
      userEnergyLevel: "medium",
      timerRunning: false,
      timeLeft: 25 * 60,
      defaultTimerMinutes: 25,
      tasksCompletedToday: 0,
      lastPlanningDate: null,
      preferences: initialPreferences,
      celebration: initialCelebration,
      resetState: initialResetState,

      // View Actions
      setView: (view) => set({ currentView: view }),

      openModal: (modal) =>
        set((state) => ({
          modals: { ...state.modals, [modal]: true },
        })),

      closeModal: (modal) =>
        set((state) => ({
          modals: { ...state.modals, [modal]: false },
        })),

      openStatModal: (type) =>
        set((state) => ({
          modals: { ...state.modals, statModal: type },
        })),

      closeStatModal: () =>
        set((state) => ({
          modals: { ...state.modals, statModal: null },
        })),

      closeAllModals: () => set({ modals: initialModals }),

      setEditingTask: (task) => set({ editingTask: task }),

      navigateAndClose: (view) =>
        set({
          currentView: view,
          modals: initialModals,
        }),

      // Session Actions
      setUserEnergy: (level) => set({ userEnergyLevel: level }),

      setTimerRunning: (running) => set({ timerRunning: running }),

      setTimeLeft: (seconds) => set({ timeLeft: seconds }),

      decrementTimer: () =>
        set((state) => ({
          timeLeft: Math.max(0, state.timeLeft - 1),
        })),

      resetTimer: () =>
        set((state) => ({
          timeLeft: state.preferences.defaultDurationMinutes * 60,
          timerRunning: false,
        })),

      incrementTasksCompleted: () =>
        set((state) => ({
          tasksCompletedToday: state.tasksCompletedToday + 1,
        })),

      completePlanning: () =>
        set({
          lastPlanningDate: new Date().toDateString(),
        }),

      shouldShowPlanning: () => {
        const state = get()
        const today = new Date().toDateString()
        return state.lastPlanningDate !== today
      },

      // Preference Actions
      updatePreference: (key, value) =>
        set((state) => ({
          preferences: { ...state.preferences, [key]: value },
        })),

      // Celebration Actions
      triggerCelebration: (ctx) =>
        set((state) => ({
          celebration: {
            isVisible: true,
            wasQuickWin: ctx.wasQuickWin ?? false,
            wasOverdue: ctx.wasOverdue ?? false,
            wasFromBreakdown: ctx.wasFromBreakdown ?? false,
            wasPostReset: ctx.wasPostReset ?? get().isPostReset(),
            stepsCompleted: ctx.stepsCompleted ?? 0,
            taskTitle: ctx.taskTitle ?? "",
          },
        })),
      dismissCelebration: () =>
        set({
          celebration: initialCelebration,
        }),

      // Reset Actions
      shameFreeReset: async () => {
        const supabase = createClient()
        const {
          data: { user },
        } = await supabase.auth.getUser()

        if (!user) {
          return { success: false, message: "Not authenticated" }
        }

        try {
          const now = new Date().toISOString()

          // Archive overdue tasks
          await supabase
            .from("tasks")
            .update({
              skipped: true,
              skip_reason: "shame_free_reset",
              updated_at: now,
            })
            .eq("user_id", user.id)
            .eq("completed", false)
            .eq("skipped", false)
            .not("deadline", "is", null)
            .lt("deadline", now)

          // Archive already skipped tasks
          await supabase
            .from("tasks")
            .update({
              skip_reason: "shame_free_reset",
              updated_at: now,
            })
            .eq("user_id", user.id)
            .eq("skipped", true)
            .is("skip_reason", null)

          // Reset streak but preserve total_completed
          await supabase
            .from("user_stats")
            .update({
              current_streak: 0,
              last_completed_date: null,
              updated_at: now,
            })
            .eq("user_id", user.id)

          set((state) => ({
            tasksCompletedToday: 0,
            lastPlanningDate: null,
            currentView: "task",
            modals: { ...state.modals, shameFreeReset: false },
            resetState: {
              lastResetDate: new Date().toISOString(),
              resetCount: state.resetState.resetCount + 1,
            },
          }))

          return { success: true }
        } catch (error) {
          console.error("[LifeOS] Shame-free reset failed:", error)
          return { success: false, message: "Reset failed. Please try again." }
        }
      },

      isPostReset: () => {
        const state = get()
        if (!state.resetState.lastResetDate) return false

        const resetDate = new Date(state.resetState.lastResetDate)
        const now = new Date()
        const hoursSinceReset = (now.getTime() - resetDate.getTime()) / (1000 * 60 * 60)

        return hoursSinceReset < 24
      },
    }),
    {
      name: "lifeos-app-store",
      partialize: (state) => ({
        currentView: state.currentView,
        userEnergyLevel: state.userEnergyLevel,
        defaultTimerMinutes: state.defaultTimerMinutes,
        lastPlanningDate: state.lastPlanningDate,
        preferences: state.preferences,
        resetState: state.resetState,
      }),
    },
  ),
)

