file.content:
.chat-interface {
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: var(--bg-primary, #ffffff);
}

.messages-container {
  flex: 1;
  overflow-y: auto;
  padding: 24px;
}

.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  color: var(--text-secondary, #666);
  text-align: center;
}

.empty-state h2 {
  margin: 0 0 8px 0;
  font-size: 24px;
  color: var(--text-primary, #333);
}

.empty-state p {
  margin: 0;
  font-size: 16px;
}

.message-group {
  margin-bottom: 32px;
}

.user-message,
.assistant-message {
  margin-bottom: 16px;
}

.message-label {
  font-size: 12px;
  font-weight: 600;
  color: var(--text-secondary, #666);
  margin-bottom: 8px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.user-message .message-content {
  background: #f0f7ff;
  padding: 16px;
  border-radius: 8px;
  border: 1px solid #d0e7ff;
  color: var(--text-primary, #333);
  line-height: 1.6;
  max-width: 80%;
  white-space: pre-wrap;
}

.loading-indicator {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 16px;
  color: var(--text-secondary, #666);
  font-size: 14px;
}

.stage-loading {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 16px;
  margin: 12px 0;
  background: var(--bg-secondary, #f9fafb);
  border-radius: 8px;
  border: 1px solid var(--border-color, #e0e0e0);
  color: var(--text-secondary, #666);
  font-size: 14px;
  font-style: italic;
}

.spinner {
  width: 20px;
  height: 20px;
  border: 2px solid var(--border-color, #e0e0e0);
  border-top-color: var(--accent-color, #4a90e2);
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

.input-form {
  display: flex;
  align-items: flex-end;
  gap: 12px;
  padding: 24px;
  border-top: 1px solid var(--border-color, #e0e0e0);
  background: var(--bg-secondary, #fafafa);
}

.message-input {
  flex: 1;
  padding: 14px;
  background: var(--bg-primary, #ffffff);
  border: 1px solid var(--border-color, #d0d0d0);
  border-radius: 8px;
  color: var(--text-primary, #333);
  font-size: 15px;
  font-family: inherit;
  line-height: 1.5;
  outline: none;
  resize: vertical;
  min-height: 80px;
  max-height: 300px;
}

.message-input:focus {
  border-color: var(--accent-color, #4a90e2);
  box-shadow: 0 0 0 3px rgba(74, 144, 226, 0.1);
}

.message-input:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  background: var(--bg-tertiary, #f5f5f5);
}

.send-button {
  padding: 14px 28px;
  background: var(--accent-color, #4a90e2);
  border: 1px solid var(--accent-color, #4a90e2);
  border-radius: 8px;
  color: #fff;
  font-size: 15px;
  font-weight: 600;
  cursor: pointer;
  transition: background 0.2s;
  white-space: nowrap;
  align-self: flex-end;
}

.send-button:hover:not(:disabled) {
  background: var(--accent-hover, #357abd);
  border-color: var(--accent-hover, #357abd);
}

.send-button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  background: #ccc;
  border-color: #ccc;
}

/* Dark mode overrides */
[data-theme="dark"] .chat-interface {
  background: var(--bg-primary);
}

[data-theme="dark"] .empty-state h2 {
  color: var(--text-primary);
}

[data-theme="dark"] .user-message .message-content {
  background: #1e3a5f;
  border-color: #2a4f7f;
  color: var(--text-primary);
}

[data-theme="dark"] .input-form {
  background: var(--bg-secondary);
  border-color: var(--border-color);
}

[data-theme="dark"] .message-input {
  background: var(--bg-primary);
  border-color: var(--border-color);
  color: var(--text-primary);
}

[data-theme="dark"] .message-input::placeholder {
  color: var(--text-secondary);
}

[data-theme="dark"] .stage-loading {
  background: var(--bg-secondary);
  border-color: var(--border-color);
}

