file.content:
.stage2 {
  background: var(--bg-secondary, #fafafa);
}

.stage2 h4 {
  margin: 20px 0 8px 0;
  color: var(--text-primary, #333);
  font-size: 14px;
  font-weight: 600;
}

.stage2 h4:first-of-type {
  margin-top: 0;
}

.stage-description {
  margin: 0 0 12px 0;
  color: var(--text-secondary, #666);
  font-size: 13px;
  line-height: 1.5;
}

.aggregate-rankings {
  background: #f0f7ff;
  padding: 16px;
  border-radius: 8px;
  margin-bottom: 20px;
  border: 2px solid #d0e7ff;
}

.aggregate-rankings h4 {
  margin: 0 0 12px 0;
  color: #2a7ae2;
  font-size: 15px;
}

.aggregate-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.aggregate-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 10px;
  background: var(--bg-primary, #ffffff);
  border-radius: 6px;
  border: 1px solid #d0e7ff;
}

.rank-position {
  color: #2a7ae2;
  font-weight: 700;
  font-size: 16px;
  min-width: 35px;
}

.rank-model {
  flex: 1;
  color: var(--text-primary, #333);
  font-family: monospace;
  font-size: 14px;
  font-weight: 500;
}

.rank-score {
  color: var(--text-secondary, #666);
  font-size: 13px;
  font-family: monospace;
}

.stage2 .tabs {
  display: flex;
  gap: 8px;
  margin-bottom: 16px;
  flex-wrap: wrap;
}

.stage2 .tab {
  padding: 8px 16px;
  background: var(--bg-primary, #ffffff);
  border: 1px solid var(--border-color, #d0d0d0);
  border-radius: 6px 6px 0 0;
  color: var(--text-secondary, #666);
  cursor: pointer;
  font-size: 14px;
  transition: all 0.2s;
}

.stage2 .tab:hover {
  background: var(--bg-tertiary, #f0f0f0);
  color: var(--text-primary, #333);
  border-color: var(--accent-color, #4a90e2);
}

.stage2 .tab.active {
  background: var(--bg-primary, #ffffff);
  color: var(--accent-color, #4a90e2);
  border-color: var(--accent-color, #4a90e2);
  border-bottom-color: var(--bg-primary, #ffffff);
  font-weight: 600;
}

.stage2 .tab-content {
  background: var(--bg-primary, #ffffff);
  padding: 16px;
  border-radius: 6px;
  border: 1px solid var(--border-color, #e0e0e0);
  margin-bottom: 20px;
}

.ranking-model {
  color: var(--text-secondary, #888);
  font-size: 12px;
  font-family: monospace;
  margin-bottom: 12px;
}

.ranking-content {
  color: var(--text-primary, #333);
  line-height: 1.6;
  font-size: 14px;
}

.parsed-ranking {
  margin-top: 16px;
  padding-top: 16px;
  border-top: 2px solid var(--border-color, #e0e0e0);
}

.parsed-ranking strong {
  color: #2a7ae2;
  font-size: 13px;
}

.parsed-ranking ol {
  margin: 8px 0 0 0;
  padding-left: 24px;
  color: var(--text-primary, #333);
}

.parsed-ranking li {
  margin: 4px 0;
  font-family: monospace;
  font-size: 13px;
}

.rank-count {
  color: var(--text-secondary, #999);
  font-size: 12px;
}

/* Dark mode overrides */
[data-theme="dark"] .stage2 {
  background: var(--bg-secondary);
}

[data-theme="dark"] .stage2 h4 {
  color: var(--text-primary);
}

[data-theme="dark"] .aggregate-rankings {
  background: #1e3a5f;
  border-color: #2a4f7f;
}

[data-theme="dark"] .aggregate-rankings h4 {
  color: #5a9ff2;
}

[data-theme="dark"] .aggregate-item {
  background: var(--bg-primary);
  border-color: #2a4f7f;
}

[data-theme="dark"] .rank-position {
  color: #5a9ff2;
}

[data-theme="dark"] .rank-model {
  color: var(--text-primary);
}

[data-theme="dark"] .stage2 .tab {
  background: var(--bg-tertiary);
  border-color: var(--border-color);
  color: var(--text-secondary);
}

[data-theme="dark"] .stage2 .tab:hover {
  background: var(--bg-secondary);
  color: var(--text-primary);
}

[data-theme="dark"] .stage2 .tab.active {
  background: var(--bg-primary);
  color: var(--accent-color);
  border-bottom-color: var(--bg-primary);
}

[data-theme="dark"] .stage2 .tab-content {
  background: var(--bg-primary);
  border-color: var(--border-color);
}

[data-theme="dark"] .ranking-content {
  color: var(--text-primary);
}

[data-theme="dark"] .parsed-ranking {
  border-color: var(--border-color);
}

[data-theme="dark"] .parsed-ranking strong {
  color: #5a9ff2;
}

[data-theme="dark"] .parsed-ranking ol {
  color: var(--text-primary);
}

/* Dissent Metrics */
.dissent-metrics {
  background: #fff8e6;
  padding: 16px;
  border-radius: 8px;
  margin-top: 20px;
  border: 2px solid #f0d090;
}

.dissent-metrics h4 {
  margin: 0 0 12px 0;
  color: #b07800;
  font-size: 15px;
}

.dissent-content {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.agreement-score,
.unanimous-winner,
.controversies,
.ranking-spread {
  display: flex;
  align-items: baseline;
  flex-wrap: wrap;
  gap: 8px;
}

.dissent-label {
  font-size: 13px;
  color: #666;
  font-weight: 500;
}

.dissent-value {
  font-size: 14px;
  font-weight: 600;
  padding: 2px 8px;
  border-radius: 4px;
}

.dissent-value.high {
  background: #dcfce7;
  color: #16a34a;
}

.dissent-value.medium {
  background: #fef9c3;
  color: #a16207;
}

.dissent-value.low {
  background: #fee2e2;
  color: #dc2626;
}

.dissent-value.winner {
  background: #dcfce7;
  color: #16a34a;
  font-family: monospace;
}

.dissent-hint {
  font-size: 12px;
  color: #888;
  font-style: italic;
}

.controversy-list {
  display: flex;
  gap: 6px;
  flex-wrap: wrap;
}

.controversy-item {
  background: #fee2e2;
  color: #dc2626;
  font-size: 13px;
  font-family: monospace;
  padding: 2px 8px;
  border-radius: 4px;
}

.ranking-spread {
  flex-direction: column;
  align-items: flex-start;
}

.spread-list {
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
  margin-top: 4px;
}

.spread-item {
  display: flex;
  align-items: center;
  gap: 4px;
  background: var(--bg-primary, #ffffff);
  padding: 4px 8px;
  border-radius: 4px;
  border: 1px solid #f0d090;
}

.spread-model {
  font-family: monospace;
  font-size: 12px;
  color: #666;
}

.spread-value {
  font-size: 12px;
  font-weight: 600;
  color: #b07800;
}

/* Dark mode - Dissent Metrics */
[data-theme="dark"] .dissent-metrics {
  background: #3d3010;
  border-color: #5a4820;
}

[data-theme="dark"] .dissent-metrics h4 {
  color: #f0c060;
}

[data-theme="dark"] .dissent-label {
  color: #aaa;
}

[data-theme="dark"] .dissent-value.high {
  background: #1a4d2e;
  color: #4ade80;
}

[data-theme="dark"] .dissent-value.medium {
  background: #4d4020;
  color: #f0c060;
}

[data-theme="dark"] .dissent-value.low {
  background: #4d1a1a;
  color: #f87171;
}

[data-theme="dark"] .dissent-value.winner {
  background: #1a4d2e;
  color: #4ade80;
}

[data-theme="dark"] .dissent-hint {
  color: #888;
}

[data-theme="dark"] .controversy-item {
  background: #4d1a1a;
  color: #f87171;
}

[data-theme="dark"] .spread-item {
  background: var(--bg-secondary);
  border-color: #5a4820;
}

[data-theme="dark"] .spread-model {
  color: #aaa;
}

[data-theme="dark"] .spread-value {
  color: #f0c060;
}

