file.content:
.settings-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
}

.settings-panel {
  background: var(--bg-primary, #ffffff);
  border-radius: 12px;
  width: 90%;
  max-width: 600px;
  max-height: 80vh;
  display: flex;
  flex-direction: column;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2);
}

.settings-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 24px;
  border-bottom: 1px solid var(--border-color, #e5e5e5);
}

.settings-header h2 {
  margin: 0;
  font-size: 1.25rem;
  color: var(--text-primary, #1a1a1a);
}

.close-button {
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: var(--text-secondary, #666);
  padding: 0;
  line-height: 1;
}

.close-button:hover {
  color: var(--text-primary, #1a1a1a);
}

.settings-content {
  flex: 1;
  overflow-y: auto;
  padding: 24px;
}

.settings-error {
  background: #fee2e2;
  color: #dc2626;
  padding: 12px;
  border-radius: 8px;
  margin-bottom: 16px;
}

.settings-success {
  background: #dcfce7;
  color: #16a34a;
  padding: 12px;
  border-radius: 8px;
  margin-bottom: 16px;
}

.settings-section {
  margin-bottom: 32px;
}

.settings-section h3 {
  margin: 0 0 8px 0;
  font-size: 1rem;
  color: var(--text-primary, #1a1a1a);
}

.settings-description {
  margin: 0 0 16px 0;
  font-size: 0.875rem;
  color: var(--text-secondary, #666);
}

/* Theme Toggle */
.theme-toggle {
  display: flex;
  gap: 8px;
}

.theme-button {
  flex: 1;
  padding: 12px 16px;
  border: 2px solid var(--border-color, #e5e5e5);
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
  cursor: pointer;
  font-size: 0.875rem;
  color: var(--text-primary, #1a1a1a);
  transition: all 0.2s;
}

.theme-button:hover {
  border-color: #4a90e2;
}

.theme-button.active {
  border-color: #4a90e2;
  background: #e8f2fd;
  color: #4a90e2;
  font-weight: 600;
}

/* Model List */
.model-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-bottom: 16px;
}

.model-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px 16px;
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
  border: 1px solid var(--border-color, #e5e5e5);
}

.model-info {
  display: flex;
  align-items: center;
  gap: 12px;
}

.model-name {
  font-family: monospace;
  font-size: 0.875rem;
  color: var(--text-primary, #1a1a1a);
}

.chairman-badge {
  background: #4a90e2;
  color: white;
  font-size: 0.75rem;
  padding: 2px 8px;
  border-radius: 4px;
  font-weight: 500;
}

.reasoning-badge {
  background: #9333ea;
  color: white;
  font-size: 0.65rem;
  padding: 2px 6px;
  border-radius: 4px;
  font-weight: 600;
  cursor: help;
}

.model-actions {
  display: flex;
  gap: 4px;
}

.model-action-btn {
  width: 28px;
  height: 28px;
  border: 1px solid var(--border-color, #e5e5e5);
  background: var(--bg-primary, #ffffff);
  border-radius: 4px;
  cursor: pointer;
  font-size: 0.875rem;
  color: var(--text-secondary, #666);
  display: flex;
  align-items: center;
  justify-content: center;
}

.model-action-btn:hover:not(:disabled) {
  background: var(--bg-secondary, #f5f5f5);
  color: var(--text-primary, #1a1a1a);
}

.model-action-btn:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

.model-action-btn.remove:hover:not(:disabled) {
  background: #fee2e2;
  color: #dc2626;
  border-color: #fecaca;
}

/* Add Model */
.add-model select {
  width: 100%;
  padding: 12px 16px;
  border: 2px dashed var(--border-color, #e5e5e5);
  border-radius: 8px;
  background: var(--bg-primary, #ffffff);
  font-size: 0.875rem;
  color: var(--text-primary, #1a1a1a);
  cursor: pointer;
  margin-bottom: 12px;
}

.add-model select:hover {
  border-color: #4a90e2;
}

.custom-model {
  display: flex;
  gap: 8px;
}

.custom-model input {
  flex: 1;
  padding: 10px 14px;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 8px;
  font-size: 0.875rem;
  font-family: monospace;
  color: var(--text-primary, #1a1a1a);
  background: var(--bg-primary, #ffffff);
}

.custom-model input:focus {
  outline: none;
  border-color: #4a90e2;
}

.custom-model button {
  padding: 10px 16px;
  background: #4a90e2;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 0.875rem;
  cursor: pointer;
}

.custom-model button:hover:not(:disabled) {
  background: #3a7bc8;
}

.custom-model button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Chairman Select */
.chairman-select {
  width: 100%;
  padding: 12px 16px;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 8px;
  background: var(--bg-primary, #ffffff);
  font-size: 0.875rem;
  font-family: monospace;
  color: var(--text-primary, #1a1a1a);
  cursor: pointer;
}

.chairman-select:focus {
  outline: none;
  border-color: #4a90e2;
}

/* Footer */
.settings-footer {
  display: flex;
  justify-content: flex-end;
  gap: 12px;
  padding: 16px 24px;
  border-top: 1px solid var(--border-color, #e5e5e5);
}

.cancel-button {
  padding: 10px 20px;
  background: none;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 8px;
  font-size: 0.875rem;
  cursor: pointer;
  color: var(--text-primary, #1a1a1a);
}

.cancel-button:hover {
  background: var(--bg-secondary, #f5f5f5);
}

.save-button {
  padding: 10px 20px;
  background: #4a90e2;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 0.875rem;
  cursor: pointer;
  font-weight: 500;
}

.save-button:hover:not(:disabled) {
  background: #3a7bc8;
}

.save-button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Settings Tabs */
.settings-tabs {
  display: flex;
  gap: 4px;
  margin-bottom: 24px;
  border-bottom: 1px solid var(--border-color, #e5e5e5);
  padding-bottom: 0;
}

.settings-tab {
  padding: 10px 20px;
  background: none;
  border: none;
  border-bottom: 2px solid transparent;
  cursor: pointer;
  font-size: 0.875rem;
  color: var(--text-secondary, #666);
  margin-bottom: -1px;
}

.settings-tab:hover {
  color: var(--text-primary, #1a1a1a);
}

.settings-tab.active {
  color: #4a90e2;
  border-bottom-color: #4a90e2;
  font-weight: 500;
}

/* Model Weights */
.weight-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.weight-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 8px 12px;
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
}

.weight-model-name {
  flex: 0 0 120px;
  font-family: monospace;
  font-size: 0.8rem;
  color: var(--text-primary, #1a1a1a);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.weight-slider {
  flex: 1;
  height: 6px;
  cursor: pointer;
}

.weight-value {
  flex: 0 0 40px;
  font-size: 0.875rem;
  font-weight: 500;
  color: #4a90e2;
  text-align: right;
}

/* Criteria List */
.criteria-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
  margin-bottom: 16px;
}

.criteria-item {
  padding: 12px 16px;
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
  border: 1px solid var(--border-color, #e5e5e5);
}

.criteria-item.disabled {
  opacity: 0.6;
}

.criteria-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 4px;
}

.criteria-toggle {
  display: flex;
  align-items: center;
  gap: 8px;
  cursor: pointer;
}

.criteria-toggle input[type="checkbox"] {
  width: 16px;
  height: 16px;
  cursor: pointer;
}

.criteria-name {
  font-weight: 500;
  color: var(--text-primary, #1a1a1a);
}

.criteria-remove {
  background: none;
  border: none;
  font-size: 1.25rem;
  color: var(--text-secondary, #666);
  cursor: pointer;
  padding: 0;
  line-height: 1;
}

.criteria-remove:hover {
  color: #dc2626;
}

.criteria-description {
  margin: 0 0 8px 24px;
  font-size: 0.8rem;
  color: var(--text-secondary, #666);
}

.criteria-weight {
  display: flex;
  align-items: center;
  gap: 8px;
  margin-left: 24px;
  font-size: 0.8rem;
  color: var(--text-secondary, #666);
}

.criteria-weight input[type="range"] {
  width: 100px;
}

.criteria-weight span:last-child {
  width: 30px;
  font-weight: 500;
  color: #4a90e2;
}

/* Add Criterion */
.add-criterion {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.add-criterion input {
  flex: 1;
  min-width: 150px;
  padding: 10px 14px;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 8px;
  font-size: 0.875rem;
  color: var(--text-primary, #1a1a1a);
  background: var(--bg-primary, #ffffff);
}

.add-criterion input:focus {
  outline: none;
  border-color: #4a90e2;
}

.add-criterion button {
  padding: 10px 16px;
  background: #4a90e2;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 0.875rem;
  cursor: pointer;
  white-space: nowrap;
}

.add-criterion button:hover:not(:disabled) {
  background: #3a7bc8;
}

.add-criterion button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Feature Toggles */
.toggle-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.toggle-item {
  display: flex;
  align-items: flex-start;
  gap: 12px;
  padding: 12px 16px;
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
  cursor: pointer;
  border: 1px solid var(--border-color, #e5e5e5);
}

.toggle-item:hover {
  border-color: #4a90e2;
}

.toggle-item input[type="checkbox"] {
  width: 18px;
  height: 18px;
  margin-top: 2px;
  cursor: pointer;
}

.toggle-info {
  display: flex;
  flex-direction: column;
  gap: 2px;
}

.toggle-name {
  font-weight: 500;
  color: var(--text-primary, #1a1a1a);
}

.toggle-description {
  font-size: 0.8rem;
  color: var(--text-secondary, #666);
}

/* Dark mode overrides */
[data-theme="dark"] .settings-panel {
  background: #1a1a1a;
}

[data-theme="dark"] .settings-header {
  border-color: #333;
}

[data-theme="dark"] .settings-section h3,
[data-theme="dark"] .settings-header h2 {
  color: #e5e5e5;
}

[data-theme="dark"] .settings-description {
  color: #999;
}

[data-theme="dark"] .theme-button {
  background: #2a2a2a;
  border-color: #444;
  color: #e5e5e5;
}

[data-theme="dark"] .theme-button.active {
  background: #1e3a5f;
  border-color: #4a90e2;
}

[data-theme="dark"] .model-item {
  background: #2a2a2a;
  border-color: #444;
}

[data-theme="dark"] .model-name {
  color: #e5e5e5;
}

[data-theme="dark"] .model-action-btn {
  background: #333;
  border-color: #444;
  color: #999;
}

[data-theme="dark"] .add-model select,
[data-theme="dark"] .custom-model input,
[data-theme="dark"] .chairman-select {
  background: #2a2a2a;
  border-color: #444;
  color: #e5e5e5;
}

[data-theme="dark"] .settings-footer {
  border-color: #333;
}

[data-theme="dark"] .cancel-button {
  border-color: #444;
  color: #e5e5e5;
}

[data-theme="dark"] .cancel-button:hover {
  background: #333;
}

[data-theme="dark"] .close-button {
  color: #999;
}

[data-theme="dark"] .close-button:hover {
  color: #e5e5e5;
}

/* Dark mode - Phase 4 elements */
[data-theme="dark"] .settings-tabs {
  border-color: #333;
}

[data-theme="dark"] .settings-tab {
  color: #999;
}

[data-theme="dark"] .settings-tab:hover {
  color: #e5e5e5;
}

[data-theme="dark"] .settings-tab.active {
  color: #4a90e2;
}

[data-theme="dark"] .weight-item,
[data-theme="dark"] .criteria-item,
[data-theme="dark"] .toggle-item {
  background: #2a2a2a;
  border-color: #444;
}

[data-theme="dark"] .weight-model-name,
[data-theme="dark"] .criteria-name,
[data-theme="dark"] .toggle-name {
  color: #e5e5e5;
}

[data-theme="dark"] .criteria-description,
[data-theme="dark"] .toggle-description,
[data-theme="dark"] .criteria-weight {
  color: #999;
}

[data-theme="dark"] .criteria-remove {
  color: #999;
}

[data-theme="dark"] .add-criterion input {
  background: #2a2a2a;
  border-color: #444;
  color: #e5e5e5;
}

/* Preset Styles */
.current-config-summary {
  padding: 16px;
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
  border: 1px solid var(--border-color, #e5e5e5);
  margin-bottom: 20px;
}

.current-config-summary h4 {
  margin: 0 0 12px 0;
  font-size: 0.9rem;
  color: var(--text-primary, #1a1a1a);
}

.config-summary-content {
  display: flex;
  gap: 24px;
  margin-bottom: 12px;
}

.config-summary-item {
  font-size: 0.85rem;
  color: var(--text-secondary, #666);
}

.config-summary-item strong {
  color: var(--text-primary, #1a1a1a);
}

.save-preset-btn {
  padding: 8px 16px;
  background: #4a90e2;
  color: white;
  border: none;
  border-radius: 6px;
  font-size: 0.85rem;
  cursor: pointer;
}

.save-preset-btn:hover:not(:disabled) {
  background: #3a7bc8;
}

.save-preset-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.save-preset-form {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.save-preset-form input {
  padding: 10px 12px;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 6px;
  font-size: 0.875rem;
  background: var(--bg-primary, #ffffff);
  color: var(--text-primary, #1a1a1a);
}

.save-preset-form input:focus {
  outline: none;
  border-color: #4a90e2;
}

.save-preset-actions {
  display: flex;
  gap: 8px;
  margin-top: 4px;
}

.save-preset-actions button {
  padding: 8px 16px;
  border-radius: 6px;
  font-size: 0.85rem;
  cursor: pointer;
}

.save-preset-actions button:first-child {
  background: #4a90e2;
  color: white;
  border: none;
}

.save-preset-actions button:first-child:hover:not(:disabled) {
  background: #3a7bc8;
}

.save-preset-actions button:first-child:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.save-preset-actions button.cancel {
  background: none;
  border: 1px solid var(--border-color, #e5e5e5);
  color: var(--text-secondary, #666);
}

.save-preset-actions button.cancel:hover {
  background: var(--bg-secondary, #f5f5f5);
}

.preset-list {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.no-presets {
  text-align: center;
  color: var(--text-secondary, #666);
  font-size: 0.9rem;
  padding: 24px;
}

.preset-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 14px 16px;
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
  border: 1px solid var(--border-color, #e5e5e5);
  transition: border-color 0.2s;
}

.preset-item:hover {
  border-color: #4a90e2;
}

.preset-item.active {
  border-color: #4a90e2;
  background: #e8f2fd;
}

.preset-info {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.preset-name {
  font-weight: 500;
  color: var(--text-primary, #1a1a1a);
  display: flex;
  align-items: center;
  gap: 8px;
}

.active-badge {
  font-size: 0.7rem;
  padding: 2px 6px;
  background: #4a90e2;
  color: white;
  border-radius: 4px;
  font-weight: 500;
}

.preset-description {
  font-size: 0.8rem;
  color: var(--text-secondary, #666);
}

.preset-details {
  font-size: 0.75rem;
  color: var(--text-tertiary, #999);
  font-family: monospace;
}

.preset-actions {
  display: flex;
  gap: 6px;
}

.apply-preset-btn {
  padding: 6px 14px;
  background: #4a90e2;
  color: white;
  border: none;
  border-radius: 6px;
  font-size: 0.8rem;
  cursor: pointer;
}

.apply-preset-btn:hover:not(:disabled) {
  background: #3a7bc8;
}

.apply-preset-btn:disabled {
  background: #ccc;
  cursor: default;
}

.delete-preset-btn {
  width: 28px;
  height: 28px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: none;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 6px;
  font-size: 1.1rem;
  color: var(--text-secondary, #666);
  cursor: pointer;
}

.delete-preset-btn:hover {
  background: #fee2e2;
  border-color: #fecaca;
  color: #dc2626;
}

/* Dark mode - Preset styles */
[data-theme="dark"] .current-config-summary {
  background: #2a2a2a;
  border-color: #444;
}

[data-theme="dark"] .current-config-summary h4 {
  color: #e5e5e5;
}

[data-theme="dark"] .config-summary-item {
  color: #999;
}

[data-theme="dark"] .config-summary-item strong {
  color: #e5e5e5;
}

[data-theme="dark"] .save-preset-form input {
  background: #333;
  border-color: #444;
  color: #e5e5e5;
}

[data-theme="dark"] .save-preset-actions button.cancel {
  border-color: #444;
  color: #999;
}

[data-theme="dark"] .save-preset-actions button.cancel:hover {
  background: #333;
}

[data-theme="dark"] .preset-item {
  background: #2a2a2a;
  border-color: #444;
}

[data-theme="dark"] .preset-item.active {
  background: #1e3a5f;
  border-color: #4a90e2;
}

[data-theme="dark"] .preset-name {
  color: #e5e5e5;
}

[data-theme="dark"] .preset-description {
  color: #999;
}

[data-theme="dark"] .preset-details {
  color: #777;
}

[data-theme="dark"] .delete-preset-btn {
  border-color: #444;
  color: #999;
}

[data-theme="dark"] .delete-preset-btn:hover {
  background: #4d1a1a;
  border-color: #7f1d1d;
  color: #f87171;
}

/* Model Parameters */
.parameters-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.parameters-item {
  padding: 14px 16px;
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
  border: 1px solid var(--border-color, #e5e5e5);
}

.parameters-model-name {
  font-family: monospace;
  font-size: 0.85rem;
  font-weight: 500;
  color: var(--text-primary, #1a1a1a);
  margin-bottom: 10px;
  display: flex;
  align-items: center;
  gap: 8px;
}

.reasoning-note {
  font-size: 0.7rem;
  color: #9333ea;
  font-family: sans-serif;
  font-weight: normal;
}

.parameters-inputs {
  display: flex;
  gap: 16px;
  flex-wrap: wrap;
}

.parameter-field {
  display: flex;
  flex-direction: column;
  gap: 4px;
  min-width: 140px;
}

.parameter-field label {
  font-size: 0.75rem;
  color: var(--text-secondary, #666);
}

.parameter-field input {
  padding: 8px 10px;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 6px;
  font-size: 0.875rem;
  background: var(--bg-primary, #ffffff);
  color: var(--text-primary, #1a1a1a);
  width: 120px;
}

.parameter-field input:focus {
  outline: none;
  border-color: #4a90e2;
}

.parameter-field input::placeholder {
  color: var(--text-tertiary, #999);
  font-size: 0.8rem;
}

/* Dark mode - Model Parameters */
[data-theme="dark"] .parameters-item {
  background: #2a2a2a;
  border-color: #444;
}

[data-theme="dark"] .parameters-model-name {
  color: #e5e5e5;
}

[data-theme="dark"] .reasoning-note {
  color: #a855f7;
}

[data-theme="dark"] .parameter-field label {
  color: #999;
}

[data-theme="dark"] .parameter-field input {
  background: #333;
  border-color: #444;
  color: #e5e5e5;
}

[data-theme="dark"] .parameter-field input::placeholder {
  color: #666;
}

/* Model Search */
.model-search-container {
  margin-top: 12px;
}

.model-search-toggle {
  padding: 8px 16px;
  background: var(--bg-secondary, #f5f5f5);
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 6px;
  cursor: pointer;
  font-size: 0.85rem;
  color: var(--text-secondary, #666);
}

.model-search-toggle:hover {
  background: #e8f2fd;
  border-color: #4a90e2;
  color: #4a90e2;
}

.model-search {
  margin-top: 12px;
  position: relative;
}

.model-search input {
  width: 100%;
  padding: 10px 14px;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 8px;
  font-size: 0.875rem;
  color: var(--text-primary, #1a1a1a);
  background: var(--bg-primary, #ffffff);
  box-sizing: border-box;
}

.model-search input:focus {
  outline: none;
  border-color: #4a90e2;
}

.search-indicator {
  position: absolute;
  right: 12px;
  top: 50%;
  transform: translateY(-50%);
  font-size: 0.8rem;
  color: var(--text-secondary, #666);
}

.search-results {
  margin-top: 8px;
  max-height: 300px;
  overflow-y: auto;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 8px;
  background: var(--bg-primary, #ffffff);
}

.search-result-item {
  padding: 10px 14px;
  cursor: pointer;
  border-bottom: 1px solid var(--border-color, #e5e5e5);
}

.search-result-item:last-child {
  border-bottom: none;
}

.search-result-item:hover {
  background: #e8f2fd;
}

.search-result-name {
  font-weight: 500;
  color: var(--text-primary, #1a1a1a);
  display: flex;
  align-items: center;
  gap: 6px;
}

.reasoning-tag {
  background: #9333ea;
  color: white;
  font-size: 0.6rem;
  padding: 1px 4px;
  border-radius: 3px;
  font-weight: 600;
}

.search-result-id {
  font-size: 0.75rem;
  font-family: monospace;
  color: var(--text-secondary, #666);
  margin-top: 2px;
}

.search-result-pricing {
  font-size: 0.7rem;
  color: var(--text-tertiary, #999);
  margin-top: 2px;
}

/* Dark mode - Model Search */
[data-theme="dark"] .model-search-toggle {
  background: #2a2a2a;
  border-color: #444;
  color: #999;
}

[data-theme="dark"] .model-search-toggle:hover {
  background: #1e3a5f;
  border-color: #4a90e2;
  color: #4a90e2;
}

[data-theme="dark"] .model-search input {
  background: #2a2a2a;
  border-color: #444;
  color: #e5e5e5;
}

[data-theme="dark"] .search-results {
  background: #2a2a2a;
  border-color: #444;
}

[data-theme="dark"] .search-result-item {
  border-color: #444;
}

[data-theme="dark"] .search-result-item:hover {
  background: #1e3a5f;
}

[data-theme="dark"] .search-result-name {
  color: #e5e5e5;
}

[data-theme="dark"] .search-result-id {
  color: #999;
}

[data-theme="dark"] .search-result-pricing {
  color: #777;
}

