file.content:
.stage {
  margin: 24px 0;
  padding: 20px;
  background: var(--bg-secondary, #fafafa);
  border-radius: 8px;
  border: 1px solid var(--border-color, #e0e0e0);
}

.stage-title {
  margin: 0 0 16px 0;
  color: var(--text-primary, #333);
  font-size: 16px;
  font-weight: 600;
}

.tabs {
  display: flex;
  gap: 8px;
  margin-bottom: 16px;
  flex-wrap: wrap;
}

.tab {
  padding: 8px 16px;
  background: var(--bg-primary, #ffffff);
  border: 1px solid var(--border-color, #d0d0d0);
  border-radius: 6px 6px 0 0;
  color: var(--text-secondary, #666);
  cursor: pointer;
  font-size: 14px;
  transition: all 0.2s;
}

.tab:hover {
  background: var(--bg-tertiary, #f0f0f0);
  color: var(--text-primary, #333);
  border-color: var(--accent-color, #4a90e2);
}

.tab.active {
  background: var(--bg-primary, #ffffff);
  color: var(--accent-color, #4a90e2);
  border-color: var(--accent-color, #4a90e2);
  border-bottom-color: var(--bg-primary, #ffffff);
  font-weight: 600;
}

.tab-content {
  background: var(--bg-primary, #ffffff);
  padding: 16px;
  border-radius: 6px;
  border: 1px solid var(--border-color, #e0e0e0);
}

.model-name {
  color: var(--text-secondary, #888);
  font-size: 12px;
  margin-bottom: 12px;
  font-family: monospace;
}

.response-text {
  color: var(--text-primary, #333);
  line-height: 1.6;
}

/* Dark mode overrides */
[data-theme="dark"] .stage {
  background: var(--bg-secondary);
  border-color: var(--border-color);
}

[data-theme="dark"] .stage-title {
  color: var(--text-primary);
}

[data-theme="dark"] .tab {
  background: var(--bg-tertiary);
  border-color: var(--border-color);
  color: var(--text-secondary);
}

[data-theme="dark"] .tab:hover {
  background: var(--bg-secondary);
  color: var(--text-primary);
}

[data-theme="dark"] .tab.active {
  background: var(--bg-primary);
  color: var(--accent-color);
  border-bottom-color: var(--bg-primary);
}

[data-theme="dark"] .tab-content {
  background: var(--bg-primary);
  border-color: var(--border-color);
}

[data-theme="dark"] .response-text {
  color: var(--text-primary);
}

/* Confidence Styles */
.tab-confidence {
  margin-left: 6px;
  font-size: 0.7rem;
  padding: 1px 4px;
  border-radius: 3px;
  font-weight: 600;
}

.tab-confidence.high {
  background: #dcfce7;
  color: #16a34a;
}

.tab-confidence.medium {
  background: #fef9c3;
  color: #a16207;
}

.tab-confidence.low {
  background: #fee2e2;
  color: #dc2626;
}

.model-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 12px;
}

.confidence-badge {
  font-size: 0.8rem;
  padding: 4px 10px;
  border-radius: 4px;
  font-weight: 500;
}

.confidence-badge.high {
  background: #dcfce7;
  color: #16a34a;
}

.confidence-badge.medium {
  background: #fef9c3;
  color: #a16207;
}

.confidence-badge.low {
  background: #fee2e2;
  color: #dc2626;
}

.confidence-summary {
  margin-top: 20px;
  padding: 16px;
  background: #f8fafc;
  border-radius: 8px;
  border: 1px solid var(--border-color, #e0e0e0);
}

.confidence-summary h4 {
  margin: 0 0 12px 0;
  font-size: 0.9rem;
  color: var(--text-primary, #333);
}

.confidence-bars {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.confidence-bar-item {
  display: flex;
  align-items: center;
  gap: 12px;
}

.confidence-bar-label {
  flex: 0 0 100px;
  font-size: 0.75rem;
  font-family: monospace;
  color: var(--text-secondary, #666);
  text-align: right;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.confidence-bar-track {
  flex: 1;
  height: 8px;
  background: #e5e7eb;
  border-radius: 4px;
  overflow: hidden;
}

.confidence-bar-fill {
  height: 100%;
  border-radius: 4px;
  transition: width 0.3s ease;
}

.confidence-bar-fill.high {
  background: #22c55e;
}

.confidence-bar-fill.medium {
  background: #eab308;
}

.confidence-bar-fill.low {
  background: #ef4444;
}

.confidence-bar-value {
  flex: 0 0 40px;
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--text-secondary, #666);
}

/* Dark mode - Confidence */
[data-theme="dark"] .tab-confidence.high {
  background: #1a4d2e;
  color: #4ade80;
}

[data-theme="dark"] .tab-confidence.medium {
  background: #4d4020;
  color: #f0c060;
}

[data-theme="dark"] .tab-confidence.low {
  background: #4d1a1a;
  color: #f87171;
}

[data-theme="dark"] .confidence-badge.high {
  background: #1a4d2e;
  color: #4ade80;
}

[data-theme="dark"] .confidence-badge.medium {
  background: #4d4020;
  color: #f0c060;
}

[data-theme="dark"] .confidence-badge.low {
  background: #4d1a1a;
  color: #f87171;
}

[data-theme="dark"] .confidence-summary {
  background: var(--bg-secondary);
  border-color: var(--border-color);
}

[data-theme="dark"] .confidence-summary h4 {
  color: var(--text-primary);
}

[data-theme="dark"] .confidence-bar-track {
  background: #374151;
}

[data-theme="dark"] .confidence-bar-fill.high {
  background: #22c55e;
}

[data-theme="dark"] .confidence-bar-fill.medium {
  background: #eab308;
}

[data-theme="dark"] .confidence-bar-fill.low {
  background: #ef4444;
}

