file.content:
.analytics-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
}

.analytics-panel {
  background: var(--bg-primary, #ffffff);
  border-radius: 12px;
  width: 90%;
  max-width: 800px;
  max-height: 85vh;
  display: flex;
  flex-direction: column;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2);
}

.analytics-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 24px;
  border-bottom: 1px solid var(--border-color, #e5e5e5);
}

.analytics-header h2 {
  margin: 0;
  font-size: 1.25rem;
  color: var(--text-primary, #1a1a1a);
}

.analytics-content {
  flex: 1;
  overflow-y: auto;
  padding: 24px;
}

.analytics-error {
  background: #fee2e2;
  color: #dc2626;
  padding: 12px;
  border-radius: 8px;
  margin-bottom: 16px;
}

.analytics-loading {
  text-align: center;
  padding: 40px;
  color: var(--text-secondary, #666);
}

.analytics-section {
  margin-bottom: 32px;
}

.analytics-section h3 {
  margin: 0 0 16px 0;
  font-size: 1rem;
  color: var(--text-primary, #1a1a1a);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

/* Stats Grid */
.stats-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
  gap: 12px;
}

.stat-card {
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
  padding: 16px;
  text-align: center;
  border: 1px solid var(--border-color, #e5e5e5);
}

.stat-card.highlight {
  background: #e8f2fd;
  border-color: #4a90e2;
}

.stat-value {
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--text-primary, #1a1a1a);
  margin-bottom: 4px;
}

.stat-card.highlight .stat-value {
  color: #4a90e2;
}

.stat-label {
  font-size: 0.75rem;
  color: var(--text-secondary, #666);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* Model Table */
.model-table {
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 8px;
  overflow: hidden;
}

.model-table-header {
  display: grid;
  grid-template-columns: 2fr 1fr 1fr 1fr 1fr 1fr;
  gap: 8px;
  padding: 12px 16px;
  background: var(--bg-secondary, #f5f5f5);
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--text-secondary, #666);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  border-bottom: 1px solid var(--border-color, #e5e5e5);
}

.model-table-row {
  display: grid;
  grid-template-columns: 2fr 1fr 1fr 1fr 1fr 1fr;
  gap: 8px;
  padding: 12px 16px;
  font-size: 0.875rem;
  color: var(--text-primary, #1a1a1a);
  border-bottom: 1px solid var(--border-color, #e5e5e5);
  cursor: pointer;
  transition: background 0.15s;
}

.model-table-row:last-child {
  border-bottom: none;
}

.model-table-row:hover {
  background: var(--bg-secondary, #f5f5f5);
}

.model-table-row.selected {
  background: #e8f2fd;
}

.col-model {
  font-family: monospace;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.col-queries,
.col-time,
.col-tokens,
.col-ranking,
.col-cost {
  text-align: right;
}

.no-data {
  text-align: center;
  padding: 40px;
  color: var(--text-secondary, #666);
  background: var(--bg-secondary, #f5f5f5);
  border-radius: 8px;
}

/* Model Details */
.model-details {
  background: var(--bg-secondary, #f5f5f5);
  padding: 16px;
  border-radius: 8px;
  border: 1px solid var(--border-color, #e5e5e5);
}

.model-details h3 {
  margin-bottom: 12px;
}

.details-close {
  background: none;
  border: none;
  font-size: 1.25rem;
  cursor: pointer;
  color: var(--text-secondary, #666);
  padding: 0;
  line-height: 1;
}

.details-close:hover {
  color: var(--text-primary, #1a1a1a);
}

.details-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
  gap: 16px;
}

.detail-group {
  background: var(--bg-primary, #ffffff);
  padding: 12px;
  border-radius: 6px;
  border: 1px solid var(--border-color, #e5e5e5);
}

.detail-group h4 {
  margin: 0 0 8px 0;
  font-size: 0.75rem;
  color: var(--text-secondary, #666);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.detail-stats {
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.detail-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.detail-label {
  font-size: 0.75rem;
  color: var(--text-secondary, #666);
}

.detail-value {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--text-primary, #1a1a1a);
}

.detail-value.error-rate-high {
  color: #dc2626;
}

/* Footer */
.analytics-footer {
  display: flex;
  justify-content: flex-end;
  gap: 12px;
  padding: 16px 24px;
  border-top: 1px solid var(--border-color, #e5e5e5);
}

.refresh-button {
  padding: 10px 20px;
  background: none;
  border: 1px solid var(--border-color, #e5e5e5);
  border-radius: 8px;
  font-size: 0.875rem;
  cursor: pointer;
  color: var(--text-primary, #1a1a1a);
}

.refresh-button:hover:not(:disabled) {
  background: var(--bg-secondary, #f5f5f5);
}

.refresh-button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.close-panel-button {
  padding: 10px 20px;
  background: #4a90e2;
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 0.875rem;
  cursor: pointer;
  font-weight: 500;
}

.close-panel-button:hover {
  background: #3a7bc8;
}

.close-button {
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: var(--text-secondary, #666);
  padding: 0;
  line-height: 1;
}

.close-button:hover {
  color: var(--text-primary, #1a1a1a);
}

/* Dark mode */
[data-theme="dark"] .analytics-panel {
  background: #1a1a1a;
}

[data-theme="dark"] .analytics-header {
  border-color: #333;
}

[data-theme="dark"] .analytics-header h2,
[data-theme="dark"] .analytics-section h3 {
  color: #e5e5e5;
}

[data-theme="dark"] .stat-card {
  background: #2a2a2a;
  border-color: #444;
}

[data-theme="dark"] .stat-card.highlight {
  background: #1e3a5f;
  border-color: #4a90e2;
}

[data-theme="dark"] .stat-value {
  color: #e5e5e5;
}

[data-theme="dark"] .stat-label,
[data-theme="dark"] .detail-label {
  color: #999;
}

[data-theme="dark"] .model-table {
  border-color: #444;
}

[data-theme="dark"] .model-table-header {
  background: #2a2a2a;
  border-color: #444;
  color: #999;
}

[data-theme="dark"] .model-table-row {
  border-color: #444;
  color: #e5e5e5;
}

[data-theme="dark"] .model-table-row:hover {
  background: #2a2a2a;
}

[data-theme="dark"] .model-table-row.selected {
  background: #1e3a5f;
}

[data-theme="dark"] .no-data {
  background: #2a2a2a;
  color: #999;
}

[data-theme="dark"] .model-details {
  background: #2a2a2a;
  border-color: #444;
}

[data-theme="dark"] .detail-group {
  background: #1a1a1a;
  border-color: #444;
}

[data-theme="dark"] .detail-group h4 {
  color: #999;
}

[data-theme="dark"] .detail-value {
  color: #e5e5e5;
}

[data-theme="dark"] .analytics-footer {
  border-color: #333;
}

[data-theme="dark"] .refresh-button {
  border-color: #444;
  color: #e5e5e5;
}

[data-theme="dark"] .refresh-button:hover:not(:disabled) {
  background: #333;
}

[data-theme="dark"] .close-button {
  color: #999;
}

[data-theme="dark"] .close-button:hover {
  color: #e5e5e5;
}

[data-theme="dark"] .details-close {
  color: #999;
}

[data-theme="dark"] .details-close:hover {
  color: #e5e5e5;
}
